"""
Prosty Kalkulator
"""

class Calc:
    def add(self, x, y):
        return x+y
    def sub(self, x, y):
        return x-y
    def product(self, x,y):
        return x*y
    def exponent(self, x, y):
        return x**y
    def power(self, x):
        return x**2
    pass




